import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faChevronRight, faChevronLeft, faSearch, faEdit, faCrop, faPalette, faSave, faFire } from '@fortawesome/free-solid-svg-icons'
import { MuiThemeProvider, createMuiTheme } from '@material-ui/core/styles';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import { Slider } from 'material-ui-slider';
import { getImages } from '../../api';


const styles = {
  title: {
    fontFamily: "'Open Sans', sans-serif",
    margin: 'auto',
    textAlign: 'left',
    padding: '24px',
    paddingBottom: '8px',
    fontWeight: '700',
    color: '#22965E',
  },
  text: {
    fontSize: '1rem',
    fontFamily: "'Open Sans', sans-serif",
    padding: '24px',
  },
  headerContainer: {
    backgroundColor: 'white'
  },
  button: {
    height: '48px',
  }
};

const theme = createMuiTheme({
  palette: {
    type: 'dark',
    primary: {
      main: '#00BCD4',
    },
    secondary: {
      main:'#607D8B'
    }
  },
});


class Editor extends Component {

  // scaleImage() {
  //     let imageHeight = this.state.results[this.state.slideIndex].height;
  //     let imageWidth = this.state.results[this.state.slideIndex].width;
  //     let divHeight = this.imageWindow.getBoundingClientRect().height;
  //     let divWidth = this.imageWindow.getBoundingClientRect().width;
  //     return imageHeight/imageWidth >= divHeight/divWidth ? 'auto 100%' : '100% auto';
  // }

  render() {
    const { classes } = this.props;
    return (
      <MuiThemeProvider theme={theme}>
        {console.log(this)}
        <div className='editorImage' style={{
          backgroundImage: `url(${this.props.history.location.pathname.replace('/editor/', '')})`,
          backgroundSize: 'auto 100%',}}/>
        <div className='editorBar'>
          <Slider max={100} min={-100} defaultValue={0} color='#607D8B' style={{width: '20vw', marginLeft: '15px',}}></Slider>
          <Slider max={100} min={-100} defaultValue={0} color='#607D8B' style={{width: '20vw', marginLeft: '15px', float: 'middle'}}></Slider>
          <Button className='editorButton' color='rgba(150,150,150,.25)' variant='contained' style={{
                marginRight: '8px',
                float: 'middle',
            }}>finish</Button>
        </div>
      </MuiThemeProvider>
    )
  }
}

export default withRouter(Editor);
